import React, { useState } from 'react';
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap';
// import { NavLink } from 'react-router-dom';

import { Image } from 'react-bootstrap';
import jbchusalogo from '../images/jbchusa-logo.png';

class NavBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showDropdown: false // Initialize showDropdown state
    };
  }

  render() {
  return (
    <div> 
      <Navbar bg="white" expand="lg">
        <Container fluid>
          {/* <Navbar.Brand href="/">Jesus Baptist Hawaii Church</Navbar.Brand> */}
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mx-auto"> {/* Center the image */}
              <Image src={jbchusalogo} alt="Honolulu" fluid style={{ width: '50%', height: 'auto' }} />
            </Nav>
            <Nav className="ms-auto" style={{ fontSize: "20px" }}>
              {/* Align NavLinks to the right */}
              <Nav.Link href="/">Home</Nav.Link>
              <NavDropdown title="About Us" id="basic-nav-dropdown">
                <NavDropdown.Item href="#action/3.1">Staff And Leadership</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.2">What We Believe</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.3">What Is Christianity?</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.4">Contact Us</NavDropdown.Item>
              </NavDropdown>
              <Nav.Link href="/newtochurch">New&nbsp;To&nbsp;Church?</Nav.Link>
              <Nav.Link href="/churchlife">Church&nbsp;Life</Nav.Link>
              <NavDropdown 
                  title="Ministries" 
                  id="basic-nav-dropdown"
                  show={this.state.showDropdown} // Use state for the dropdown
                  onMouseEnter={() => this.setState({ showDropdown: true })} // Open dropdown on hover
                  onMouseLeave={() => this.setState({ showDropdown: false })} // Close dropdown when mouse leaves
                >
                  <NavDropdown.Item href="#action/3.1">Church School</NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.2">Youth Group</NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.3">Young Adult Group</NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.4">Mother's Group</NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.5">Father's Group</NavDropdown.Item>
                </NavDropdown>

              <NavDropdown 
                  title="Our M" 
                  id="basic-nav-dropdown"
                  show={this.state.showDropdown} // Use state for the dropdown
                  onMouseEnter={() => this.setState({ showDropdown: true })} // Open dropdown on hover
                  onMouseLeave={() => this.setState({ showDropdown: false })} // Close dropdown when mouse leaves
                >
                <NavDropdown.Item href="#action/3.1">USA Conference</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.2">Life World Mission</NavDropdown.Item>
              </NavDropdown>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </div>

    )
  }
}

export default NavBar;
