import React, { useState } from 'react';
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap';
// import { NavLink } from 'react-router-dom';

import { Image } from 'react-bootstrap';
import jbchusalogo from '../images/jbchusa-logo.png';

// Custom hook to manage dropdown state
function useDropdownState(initialState = false) {
  const [isOpen, setIsOpen] = useState(initialState);

  const openDropdown = () => setIsOpen(true);
  const closeDropdown = () => setIsOpen(false);

  return { isOpen, openDropdown, closeDropdown };
}

// Dropdown component
function Dropdown({ title, items }) {
  const { isOpen, openDropdown, closeDropdown } = useDropdownState(false);

  return (
    <NavDropdown
      title={title}
      id="basic-nav-dropdown"
      show={isOpen}
      onMouseEnter={openDropdown}
      onMouseLeave={closeDropdown}
    >
      {items.map((item, index) => (
        <NavDropdown.Item key={index} href={`#action/${index + 3}`}>
          {item}
        </NavDropdown.Item>
      ))}
    </NavDropdown>
  );
}

// Navbar component
function NavbarComponent() {
  return (
    <div>
      <Navbar bg="white" expand="lg">
        <Container fluid>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mx-auto">
              <Image src={jbchusalogo} alt="Honolulu" fluid style={{ width: '50%', height: 'auto' }} />
            </Nav>
            <Nav className="ms-auto" style={{ fontSize: '20px' }}>
              <Nav.Link href="/">Home</Nav.Link>
              <Dropdown title="About Us" items={['Staff And Leadership', 'What We Believe', 'What Is Christianity?', 'Contact Us']} />
              <Nav.Link href="/newtochurch">New To Church?</Nav.Link>
              <Nav.Link href="/churchlife">Church Life</Nav.Link>
              <Dropdown title="Ministries" items={['Church School', 'Youth Group', 'Young Adult Group', "Mother's Group", "Father's Group"]} />
              <Dropdown title="Our Mission" items={['USA Conference', 'Life World Mission']} />
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </div>
  );
}

export default Navbar;
